library(dplyr)
library(readr)
library(tidyr)
library(gt)
library(gtExtras)
library(ggplot2)

taylor &lt;-
  read_rds("data/combined_artist_data.rds") |&gt; 
  filter(artist == "Taylor Swift",
         stats.source == "spotify") |&gt; 
  mutate(date = as.Date(date)) |&gt; 
  pivot_longer(cols = followers_total:monthly_listeners_current)

taylor |&gt;
  group_by(name) |&gt;
  dplyr::summarize(spotify_data = list(value),
                   .groups = "drop") |&gt;
  gt() |&gt;
  gt_plt_sparkline(spotify_data)

taylor |&gt;
  ggplot(aes(x = date, y = value, group = name)) + geom_line() +
  facet_grid(~ name)
